import React from "react";
import { useEffect , useState } from 'react';
import axios from "axios";
// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Modal,
  Container,
  Form,
  Row,
  Col,
} from "react-bootstrap";

function TableList() {

  const [categorys, setCategory] = useState([]);
  const [showModal, setShowModal] = React.useState(false);
  const [inputField , setInputField] = useState({
        name: '',
        price: '',
        id: 0,
  })

  const inputsHandler = (e) =>{
        setInputField( {[e.target.name]: e.target.value} )
  }


    const submitButton = () =>{
        alert(inputField.first_name)
    }

  useEffect(() => {
    getAllCategorys();
  } ,[]);

  const getAllCategorys = () => {
    axios.get("http://127.0.0.1:8000/api/categorys/")
    .then((res) => setCategory(res.data))
    .catch((err) => console.log(err));
  } ;

  const handleSubmit = () => {
    console.log("ddd");

  }

  return (
    <>
      <Container fluid>
        <Row>
          <Col md="12">
            <Card className="card-plain table-plain-bg">
              <Card.Header>
                <Card.Title as="h4">Category</Card.Title>
              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Button
                  className="btn-fill btn-wd"
                  variant="info"
                  onClick={() => setShowModal(true)}
                >
                  Create
                </Button>
                <Table className="table-hover">
                  <thead>
                    <tr>
                      <th className="border-0">ID</th>
                      <th className="border-0">Name</th>
                      <th className="border-0">Price</th>
                      <th className="border-0">Action</th>
                    </tr>
                  </thead>
                  <tbody>
                    {categorys.map(category => 
                      <tr key={category.id}>
                        <td>{category.id}</td>
                        <td>{category.name}</td>
                        <td>{category.price}</td>
                        <td>action</td>
                      </tr>
                    )}
                  </tbody>
                </Table>
              </Card.Body>
            </Card>
          </Col>
        </Row>

      </Container>
      {/* Mini Modal */}
      <Modal
          className="modal-mini modal-primary"
          show={showModal}
          onHide={() => setShowModal(false)}
      >
          <Modal.Header className="justify-content-center">
            <div className="modal-profile">
              <i className="nc-icon nc-cart-simple"></i>
            </div>
          </Modal.Header>

          <Modal.Body >
            <Form >
              <Form.Group className="mb-3" controlId="name" name="name">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" placeholder="Enter category name" onChange={inputsHandler} value={inputField.name}/>
              </Form.Group>

              <Form.Group className="mb-3" controlId="price">
                <Form.Label>Price</Form.Label>
                <Form.Control type="number" placeholder="Price" name="price" onChange={inputsHandler} value={inputField.price} />
              </Form.Group>
              <Button variant="btn-simple" onClick={submitButton}>
                Submit
              </Button>
              <Button
                variant="btn-simple"
                onClick={() => setShowModal(false)}
              >Close</Button>
            </Form>
          </Modal.Body>
      </Modal>
      {/* End Modal */}
    </>
  );
}

export default TableList;
